import "./lodging.css";
import Dropdown from "../Dropdown/dropdown";


function LodgDesc( { id, title, location, description, equipments, tags, host, rating } ) {
    const equipementList = equipments.map((equipment) => <li>{equipment}</li> );
    return (

        <section className="lodging-dropdown">
        <Dropdown
            title="Description"
            contents= {description}
            />
        <Dropdown
            title="Equipements"
            contents={equipementList}
            />
    </section>
    )
}

export default LodgDesc;
