import "./lodging.css";
import { useState } from "react";
import chevronPrev from "../../assets/chevronPrev.png";
import chevronNext from "../../assets/chevronNext.png";

function Carrousel({ pictures, title }) {
    let [index, setIndex] = useState(0);

    const Previous = () => {
      if (index === 0) {
        setIndex(pictures.length - 1);
      } else {
        setIndex(index - 1);
      }
    };

    const Next = () => {
      if (index === pictures.length - 1) {
        setIndex(0);
      } else {
        setIndex(index + 1);
      }
    };

    return (
      <div className="container_carrousel">
        <img onClick={Previous} className={pictures.length > 1 ? "chevron chevron_left" : ""} src={chevronPrev} alt="précédent" />
        <img src={pictures[index]} alt={title} className="picture_carrousel" />
        <img onClick={Next} className={pictures.length > 1 ? "chevron chevron_right" : ""} src={chevronNext} alt="suivant" />
      </div>
    );
  }

export default Carrousel;
